import React, { Component } from "react";
import * as $ from "jquery";
import { authEndpoint, clientId, redirectUri, scopes } from "./config";
import hash from "./hash";
import Player from "./Player";
import QueueList from "./QueueList";
import "./App.css";
import firebase from '../../firebase';

class App extends Component {
  constructor() {
    super();
    this.state = {
      songSearchText: '',
      songIdToAdd: '',
      songSearchResults: [],
      queue: [],
      currentSong: null,
      nextSong: null,
      token: null,
      item: {
        album: {
          images: [{ url: "" }]
        },
        name: "",
        artists: [{ name: "" }],
        duration_ms: 0
      },
      is_playing: "Paused",
      progress_ms: 0
    };
    this.getCurrentlyPlaying = this.getCurrentlyPlaying.bind(this);
  }

  updateQueue(){
    const queueRef = firebase.database().ref('queue');

    queueRef.on('value', snapshot => {
      const getQueue = snapshot.val();
      let ascQueue = [];
      let current = null;
      let next = null;
      var i = 0;
      for(let song in getQueue){
        if(song !== undefined){
          //if this is the first song, it's currently playing
          if (i == 0){
            //set current song to this title
          }
          //if this is the second song, it's next
          else if (i == 1){
            //set next song to this title
          }
          ascQueue.push({
            //add this song to ascQueue
          });
        }
        i++;
      }
      const queue = getQueue.reverse();
      this.setState({queue: queue});
    });
  }

  componentDidMount() {
    // Set token
    let _token = hash.access_token;

    this.interval = window.setInterval(
      (function () {
        if (_token) {
          // Set token
          this.setState({
            token: _token
          });
          //this.updateQueue(_token);
          this.getCurrentlyPlaying(_token);
        }
      }).bind(this),
      5000
    )
  }

  getCurrentlyPlaying(token) {
    // Make a call using the token
    $.ajax({
      url: "https://api.spotify.com/v1/me/player",
      type: "GET",
      beforeSend: xhr => {
        xhr.setRequestHeader("Authorization", "Bearer " + token);
      },
      success: data => {
        if (data != undefined){
          this.setState({
            item: data.item,
            is_playing: data.is_playing,
            progress_ms: data.progress_ms
          });
        }
        else {
          this.state = {
            token: null,
            item: {
              album: {
                images: [{ url: "" }]
              },
              name: "",
              artists: [{ name: "" }],
              duration_ms: 0
            },
            is_playing: "Paused",
            progress_ms: 0
          };
        }
      }
    });
  }

  addSong = e =>{
    e.preventDefault();
    console.log("Add Song clicked. ID: " + e.target.title);
    //add the song to the queue
    const queueRef = firebase.database().ref('queue');
    const songInfo = {
      songTitleAndArtist: e.target.innerText,
      songId: e.target.title
    }
    //update the queue to show the changes
  }

  handleSearch = e => {
    this.setState({[e.target.name]: e.target.value});
  }

  handleSearchSubmit = e => {
    e.preventDefault();
    if(this.state.songSearchText !== ''){
      //search for the song
      //set results to results
      //update the list
      $.ajax({
        url: "https://api.spotify.com/v1/search?q=" + this.state.songSearchText + "&type=track&limit=10",
        type: "GET",
        beforeSend: xhr => {
          xhr.setRequestHeader("Authorization", "Bearer " + this.state.token);
        },
        success: data => {
          if (data != undefined){
            console.log(data.tracks.items[0].id);
            let ascResults = [];
            for (var i = 0; i < 10; i++){
              ascResults.push({
                songTitle: data.tracks.items[i].name,
                songArtist: data.tracks.items[i].artists[0].name,
                songId: data.tracks.items[i].id
              })
            }
            this.setState({songSearchResults: ascResults});
            console.log(this.state.songSearchResults);

            //Now show a list of the results
            let songs = this.state.songSearchResults;
            var ul = document.getElementById("resultsList");
            //remove all children first
            while(ul.firstChild) ul.removeChild(ul.firstChild);
            for (var i = 0; i < 10; i++){
              var li = document.createElement("li");
              li.setAttribute("id", "songResult");
              li.setAttribute("title", songs[i].songId);
              li.addEventListener("click", this.addSong);
              li.appendChild(document.createTextNode(songs[i].songTitle + " by " + songs[i].songArtist));
              ul.appendChild(li);
            }

            // this.setState({
            //   item: data.item,
            //   is_playing: data.is_playing,
            //   progress_ms: data.progress_ms
            // });
          }
          else {
            //it didn't work
          }
        }
      });

      //reset search field
      this.setState({songSearchText: ''});
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          {!this.state.token && (
            <a
              className="btn btn--loginApp-link"
              href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join(
                "%20"
              )}&response_type=token&show_dialog=true`}
            >
              Login to Spotify
            </a>
          )}
          {this.state.token && (
            <div>
              <Player
                item={this.state.item}
                is_playing={this.state.is_playing}
                progress_ms={this.progress_ms}
              />
              <QueueList />
              <div className="searchsong">
                <div className="main-wrapper">
                  <form className="send-song" onSubmit={this.handleSearchSubmit}>
                    <input type="text" name="songSearchText" id="searchText" value={this.state.songSearchText} onChange={this.handleSearch} placeholder='Search for a song' />
                  </form>
                  <div className="container">
                    <ul className='result-list' id='resultsList'>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          )}
        </header>
      </div>
    );
  }
}

export default App;
